import React, { useState, useEffect } from 'react';

const Dashboard = ({ onLogout }) => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    }
  }, []);

  return (
    <div className="dashboard">
      <button className="logout-btn" onClick={onLogout}>
        Logout
      </button>

      <div className="container">
        <div className="dashboard-header">
          <h1>Welcome to Recipe Hub</h1>
          <p>Hello {user?.name}! Ready to discover amazing recipes?</p>
        </div>

        <div className="dashboard-content">
          <div style={{
            background: 'rgba(255, 255, 255, 0.95)',
            backdropFilter: 'blur(10px)',
            borderRadius: '15px',
            padding: '30px',
            boxShadow: '0 10px 30px rgba(0, 0, 0, 0.1)',
            textAlign: 'center'
          }}>
            <h2 style={{ marginBottom: '20px', color: '#2d3748' }}>
              Your Recipe Journey Starts Here
            </h2>
            <p style={{ color: '#718096', fontSize: '16px', lineHeight: '1.6' }}>
              You've successfully logged into Recipe Hub! This is your dashboard where you can 
              manage your recipes, discover new dishes, and connect with fellow food enthusiasts.
            </p>
            <div style={{ 
              marginTop: '30px', 
              padding: '20px', 
              background: '#f7fafc', 
              borderRadius: '10px',
              border: '1px solid #e2e8f0'
            }}>
              <h3 style={{ color: '#4a5568', marginBottom: '10px' }}>Account Details</h3>
              <p><strong>Name:</strong> {user?.name}</p>
              <p><strong>Email:</strong> {user?.email}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
